import React from 'react';
import Cart from '../Cart/Cart';
import classes from './Modal.module.css';

const Modal = (props) => {
  return (
    <div className={classes.backdrop}>
      <div className={classes.modal}>{props.children}</div>
    </div>
  );
};

export default Modal;
